import React from "react";
import styles from "./Footer.module.css";

const Footer = (props) => {
  const data = [
    {
      col_number: 1,
      col_values: [
        "Countries",
        "Regions",
        "Cities",
        "Districts",
        "Airports",
        "Hotels",
      ],
    },
    {
      col_number: 2,
      col_values: [
        "Homes",
        "Apartments",
        "Resorts",
        "Villas",
        "Hostels",
        "Guest houses",
      ],
    },
    {
      col_number: 3,
      col_values: [
        "Unique places to stay",
        "Reviews",
        "Unpacked: Travel articles",
        "Travel communities",
        "Seasonal and holiday deals",
      ],
    },
    {
      col_number: 4,
      col_values: [
        "Car rental",
        "Flight Finder",
        "Restaurant reservations",
        "Travel Agents",
      ],
    },
    {
      col_number: 5,
      col_values: [
        "Curtomer Service",
        "Partner Help",
        "Careers",
        "Sustainability",
        "Press center",
        "Safety Resource Center",
        "Investor relations",
        "Terms & conditions",
      ],
    },
  ];

  const cols = data.map((col) => {
    const className = styles.col_ + col.col_number;
    const number = col.col_number;
    return (
      <div className={`${className}`}>
        <ul>
          {col.col_number === number
            ? col.col_values.map((str) => {
                return <li>{str}</li>;
              })
            : ""}
        </ul>
      </div>
    );
  });

  return cols;
};

export default Footer;
