import React from "react";
import styles from "./HotelsList.module.css";

const HotelsList = (props) => {
  const data = [
    {
      name: "Aparthotel Stare Miasto",
      city: "Madrid",
      price: 120,
      rate: 8.9,
      type: "Excellent",
      image_url: "./images/hotel_1.webp",
    },
    {
      name: "Comfort Suites Airport",
      city: "Austin",
      price: 140,
      rate: 9.3,
      type: "Exceptional",
      image_url: "./images/hotel_2.jpg",
    },
    {
      name: "Four Seasons Hotel",
      city: "Lisbon",
      price: 99,
      rate: 8.8,
      type: "Excellent",
      image_url: "./images/hotel_3.jpg",
    },
    {
      name: "Hilton Garden Inn",
      city: "Berlin",
      price: 105,
      rate: 8.9,
      type: "Excellent",
      image_url: "./images/hotel_4.jpg",
    },
  ];

  const hotels = data.map((hotel) => {
    return (
      <div className={styles.container}>
        <img
          className={styles.image}
          src={hotel.image_url}
          alt={hotel.name}
        ></img>
        <a href="./detail">
          <h2 className={styles.h2}>{hotel.name}</h2>
        </a>
        <h3 className={styles.h3}>{hotel.city}</h3>
        <p className={styles.price}>Starting from {`${hotel.price}`}$</p>
        <div>
          <p className={styles.ratePoint}>{hotel.rate}</p>
          <p className={styles.rating}>{hotel.type}</p>
        </div>
      </div>
    );
  });

  return (
    <div>
      <div className={styles.col_2} />
      <div className={styles.col_8}>
        <h1 className={styles.h1}>Homes guests love</h1>
        {hotels}
      </div>
      <div className={styles.col_2} />
    </div>
  );
};

export default HotelsList;
